import groovy.sql.Sql

def gdb = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("Database");
def database;
if (gdb == null) {
database =
testRunner.testCase.testSuite.project.getPropertyValue("Database");
}else{
database = gdb;
}
def username = testRunner.testCase.testSuite.project.getPropertyValue("Username")
def password = testRunner.testCase.testSuite.project.getPropertyValue("Password")

def sql = Sql.newInstance(database, username, password) 

sql.execute("delete from FD.ENVIRONMENT_INSTANCE where ENVIRONMENT_ID in (select ENVIRONMENT_ID from FD.ENVIRONMENT where ENVIRONMENT_CODE like 'POST%')")
sql.execute("delete from FD.ENVIRONMENT_INSTANCE where ENVIRONMENT_ID in (select ENVIRONMENT_ID from FD.ENVIRONMENT where ENVIRONMENT_CODE like 'INSTANCE%')")
sql.execute("delete from FD.ENVIRONMENT_INSTANCE where ENVIRONMENT_ID in (select ENVIRONMENT_ID from FD.ENVIRONMENT where ENVIRONMENT_CODE like 'PUT%')")
sql.execute("delete from FD.ENVIRONMENT_INSTANCE where ENVIRONMENT_ID in (select ENVIRONMENT_ID from FD.ENVIRONMENT where ENVIRONMENT_CODE like 'PATCH%')")

sql.execute("delete from FD.ENVIRONMENT where ENVIRONMENT_CODE like 'INSTANCE%'")
sql.execute("delete from FD.ENVIRONMENT where ENVIRONMENT_CODE like 'POST%'")
sql.execute("delete from FD.ENVIRONMENT where ENVIRONMENT_CODE like 'PUT%'")
sql.execute("delete from FD.ENVIRONMENT where ENVIRONMENT_CODE like 'PATCH%'")


sql.execute("delete from FD.INSTANCE where INSTANCE_CODE like 'INSTANCE%'")

